import React, { useState } from 'react';
import { Form, Header, Button } from 'semantic-ui-react';
import { useDispatch, useSelector } from "react-redux";
import { Header as Head } from '../TOOLS/HEADER/Header';

export const LoginForm = (props) => {

    const dispatch = useDispatch();

    const [form, setLoginForm] = useState({
        surname: "",
        pwd: "",
    });

    const data = JSON.stringify({
        "surname": form.surname,
        "pwd": form.pwd
    });

    let callbackUserLogin = (response) => {
        if (response.status === 200) {
            localStorage.setItem('archi_user', response.response)
            window.location = '/'
        } else {
            let error = document.querySelector('#error');

        }
    }

    const handleChange = (event) => {
        const key = event.target.name;
        const value = event.target.value;
        setLoginForm({ ...form, [key]: value });
    };

    const context = {
        method: 'POST',
        body: data,
        headers: {
            'Content-type': 'application/json; charset=UTF-8'
        }
    };

    const handleSubmit = () => {

        fetch("http://localhost:8080/login", context)
            .then(response => response.json()
                // .then(data => ({ status: response.status, response: data.response }))
                .then(data => callbackUserLogin({ status: response.status, response: data })))
            .catch(error => console.log('error', error));

    }

    return (
        <>
            <Head title={"Login"} />
            <div style={{ marginLeft: "25%", marginRight: "25%" }}>
                <Form>
                    <Header as='h4' dividing>
                        User login
                    </Header>
                    <Form.Field>
                        <Form.Input fluid label='Surname' placeholder='Surname' name="surname" onChange={handleChange} value={form.surname} />
                    </Form.Field>
                    <Form.Field>
                        <Form.Input type="password" label="Pwd" placeholder="" onChange={handleChange} name="pwd" value={form.pwd} />
                    </Form.Field>
                    <Button type='submit' onClick={handleSubmit}>Submit</Button>
                </Form>
            </div>
        </>
    );

}
