import React, { useState } from 'react';
import { Segment, Grid, Icon, Image, Form, Header as Head, Button } from 'semantic-ui-react'
import { useDispatch, useSelector } from "react-redux";

export const Header = (props) => {
    const [currentUser, setCurrentUser] = useState({
        id: "",
        surname: "theyoo",
        lastname: "",
        img: "",
        login: "",
        pwd: "",
        money: 100,

    });

    return (
        <Segment placeholder>
            <Grid columns={3} relaxed='very' stackable>
                <Grid.Column>
                    {currentUser.money}
                </Grid.Column>

                <Grid.Column>
                    <div>
                        <Head as='h2' icon textAlign='center'>
                            <Icon name='users' circular />
                            <Head.Content>{props.title}</Head.Content>
                        </Head>
                    </div>
                </Grid.Column>

                <Grid.Column>
                    {currentUser.surname}
                </Grid.Column>

            </Grid>
        </Segment>
    );

}
