import React, { useState } from 'react';
import { Segment, Grid, Icon, Button } from 'semantic-ui-react'
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

export const Header = ({ title = "Main" }) => {
    let current_user = useSelector(state => state.userReducer.user);
    cons

    let getInfoUser = () => {
        if (current_user.id) {
            return (
                <h1>{current_user.surname}</h1>
            )
        }
        return (
            <Button.Group>
                <Link to="/login"><Button>Login</Button></Link>
                <Button.Or text='or' />
                <Link to="/register"><Button color="blue">Register</Button></Link>
            </Button.Group>
        )
    }

    let getSolde = () =>{
        if (current_user.id){
            return <h1>{current_user.money} $</h1>
        }
    }

    return (
        <Segment size="large">
            <Grid columns="equal">
                <Grid.Column verticalAlign="middle" textAlign="center">
                    {getSolde()}
                </Grid.Column>

                <Grid.Column verticalAlign="middle" textAlign="center">
                    <Link to="/"><Icon size="big" name='users' circular /></Link>
                    <h1>{title}</h1>
                </Grid.Column>

                <Grid.Column verticalAlign="middle" textAlign="center">
                    {getInfoUser()}
                </Grid.Column>
            </Grid>
        </Segment>
    );

}
