import React, { useState } from 'react';
import { Form, Header, Button, Icon, Label, Menu, TableContainer } from 'semantic-ui-react'
import { useDispatch, useSelector } from "react-redux";
import { RowCard } from './cards/RowCard';

export const Table = (props) => {
    const [object, setCards] = useState([{}, {}]);

    const displayHeader = () => {
        return (
            displayCardHeader()
        )
    }

    const displayObject = () => object.map((item, index) => <RowCard key={index} item={item} />)

    const displayCardHeader = () => {
        return (
            <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>Name</Table.HeaderCell>
                    <Table.HeaderCell>Description</Table.HeaderCell>
                    <Table.HeaderCell>Family</Table.HeaderCell>
                    <Table.HeaderCell>Hp</Table.HeaderCell>
                    <Table.HeaderCell>Energy</Table.HeaderCell>
                    <Table.HeaderCell>Defense</Table.HeaderCell>
                    <Table.HeaderCell>Attack</Table.HeaderCell>
                    <Table.HeaderCell>Price</Table.HeaderCell>
                </Table.Row>
            </Table.Header>
        )
    }

    const displayRoomHeader = () => {
        return (
            <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>Room</Table.HeaderCell>
                    <Table.HeaderCell>User</Table.HeaderCell>
                    <Table.HeaderCell>Bet</Table.HeaderCell>
                    <Table.HeaderCell>Go</Table.HeaderCell>
                </Table.Row>
            </Table.Header>
        )
    }

    return (
        <Table celled textAlign="center">
            <Table.Header>
                {displayHeader()}
            </Table.Header>
            <Table.Body>
                {displayObject()}
            </Table.Body>
        </Table>
    );
}
